import{_ as t}from"./plugin-vue_export-helper-c27b6911.js";import{r as a,o as i,c as l,b as r,f as p,e as s,d as o,a as d}from"./app-b6e19e4c.js";const h={},f={href:"https://github.com/inoutcode/bitcoin_book_2nd/blob/master/%E7%AC%AC%E4%B8%80%E7%AB%A0.asciidoc#satoshi_whitepaper",target:"_blank",rel:"noopener noreferrer"};function m(u,e){const n=a("ExternalLinkIcon");return i(),l("div",null,[e[3]||(e[3]=r("p",null,"这一系列笔记是我阅读《精通比特币》时的阅读概要，意图让大家快速理解比特币(虽然很多教学视频已经做了这样的工作)。",-1)),e[4]||(e[4]=r("p",null,"本系列笔记有些地方会加入一些代码方便读者更深入理解原理。",-1)),p(" more "),e[5]||(e[5]=s('<h1 id="概述-简介比特币" tabindex="-1"><a class="header-anchor" href="#概述-简介比特币" aria-hidden="true">#</a> 概述-简介比特币</h1><h2 id="什么是比特币" tabindex="-1"><a class="header-anchor" href="#什么是比特币" aria-hidden="true">#</a> 什么是比特币？</h2><p>比特币是区块链技术的第一个应用，时至今日(2023年)已然成为了当今世界上不可忽视的全球自由资本市场的子集。当然这个子集很特殊，特殊到你的手机都可以运行一个比特币的全节点，只要存储空间足够。</p><p>用户可以通过比特币完成传统货币可以完成的任何事情，包括购买、出售、借贷等。</p><p>但是与传统货币不同，比特币是完全虚拟的，比特币隐含在发起者向接受者的交易中的，用户通过自己掌握的私钥来证明自己拥有比特币的所有权。</p><p>比特币是一个点对点的分布式系统，系统中不存在中央服务器或控制点。 比特币是通过一个叫做挖矿(mint)的过程创造的，矿工在处理比特币交易时通过大量计算竞猜一个数学题的答案。 比特币网络的任何参与者（使用一个设备运营比特币完整协议栈的用户）都可以成为矿工，利用他们电脑的处理能力来验证和记录交易。 平均每隔10分钟，会有一个矿工计算出数学题的答案，可以验证过去10分钟的交易，并且获得新发行的比特币奖励。 实质上，比特币将中央银行的货币发行和清算功能进行了去中心化，不再需要任何中央银行了。</p><p>比特币协议中内置了调用网络中挖矿方法的算法，难度是动态调整的，所以无论网络中有多少矿工，平均10分钟都会有一个矿工成功挖到矿，比特币的总量为2100个，每隔4年，比特币的发行速率都会减半，到2140年，所有比特币发行完毕。 由于比特币的发行速度递减，长期来看，比特币是货币通缩的。</p><p>比特币是协议的名称，是一个点对点网络，也是分布式计算的创新。 比特币货币本身只是这项创新的第一个应用。 比特币代表了数十年密码学和分布式系统研究的一个高峰，以独特而强大的方式将四项关键创新融合在一起：</p><ul><li><p>去中心化的点对点网络（比特币协议）</p></li><li><p>公开的账本（区块链）</p></li><li><p>一套独立交易验证和货币发行的规则（共识协议）</p></li><li><p>在有效的区块链上达成全球分散共识的机制（PoW工作证明机制）</p></li></ul><h2 id="比特币的历史" tabindex="-1"><a class="header-anchor" href="#比特币的历史" aria-hidden="true">#</a> 比特币的历史</h2>',10)),r("p",null,[e[1]||(e[1]=o("从中本聪在2008年发表的比特币白皮书开始")),r("a",f,[e[0]||(e[0]=o("比特币白皮书")),d(n)]),e[2]||(e[2]=o(",在借鉴了b-money和HashCash等之前的几个发明后，创造了完全去中心化的虚拟货币系统。关键性创新是通过POW(工作量证明)每10分钟进行一次选举，使整个网络的交易状态达成共识，这个方法优雅地解决了双重支付(双花)的问题。"))]),e[6]||(e[6]=r("p",null,"中本聪的发明也是一个分布式计算问题的实践和创新：“拜占庭将军问题”。简单来说，这个问题涉及试图在不可靠且可能受损的网络上交换信息来达成行动方案或系统状态的统一。 中本聪的Proof-of-Work的方案，不需要中心化的可信机构，即可达成共识，代表了分布式计算领域的突破，有着超越数字货币的广泛适用性。 它可以用于在去中心化网络中达成共识，证明选举，抽奖，资产登记，数字公证等的公平性。",-1)),e[7]||(e[7]=r("h2",{id:"钱包",tabindex:"-1"},[r("a",{class:"header-anchor",href:"#钱包","aria-hidden":"true"},"#"),o(" 钱包")],-1))])}const k=t(h,[["render",m],["__file","01-intro.html.vue"]]);export{k as default};
