import{_ as r}from"./plugin-vue_export-helper-c27b6911.js";import{r as o,o as s,c as l,f as c,b as e,d as t,a as n,w as d,e as a}from"./app-a4fba6dc.js";const _={},h=e("p",null,"以现在的眼光来看，你会觉得封面中图片中造型很酷吗？没错这就是二十年前年轻人群体之间最流行的造型。",-1),m=e("p",null,"与封面图片类似，现在你在短视频中看到的各种炫酷的汽车，在五年后十年后也都会变得很土。",-1),u=e("p",null,"现在的潮流，从长远来看也会变得杀马特。",-1),f=e("p",null,"与热点类似，现在的热点在未来看都是类似，你还能想起来十年前的热点吗？",-1),k=e("p",null,"大多数人连一个月/一年前的热点都记不起来，相反如果让他们回忆起十年前的热点，他们会感到非常惊讶。",-1),B=e("p",null,"在过去没有互联网的时代里，闹市里哪里有热点可凑，哪里就会挤满了人群。",-1),E=e("figure",null,[e("img",{src:"https://image.betxin.top/file/7dd0f5afb23d0bb2e4210.jpg",alt:"image.png",tabindex:"0",loading:"lazy"}),e("figcaption",null,"image.png")],-1),g=e("p",null,"现在人们的注意力大多都在互联网上，大多数人的注意力也会被各种各样的热点所占据。",-1),x=e("p",null,"Wikipedia上有一份中国大陆网络热点事件列表:",-1),b={href:"https://zh.wikipedia.org/wiki/%E4%B8%AD%E5%9B%BD%E5%A4%A7%E9%99%86%E7%BD%91%E7%BB%9C%E7%83%AD%E7%82%B9%E4%BA%8B%E4%BB%B6%E5%88%97%E8%A1%A8",target:"_blank",rel:"noopener noreferrer"},A=a('<p>上面记载了从95年至今约30年时间里，互联网自普及以来所有的中国大陆的网络热点。想想这些热点你都有没有参与过。</p><p>我猜大多数人都有，或者间接被周围的人带入过这些热点。</p><p><mark>热点总会出现</mark></p><p>现在想一想，如果你近十年的热点你都没有参与，也没有关心过所有这些热点，你的生活会变得更好吗？</p><p>我猜哪怕没有变得更好，最起码不会变得比现在更差，因为你没有将宝贵的注意力置入终将毫无意义的热点中。</p><h1 id="避免热点" tabindex="-1"><a class="header-anchor" href="#避免热点" aria-hidden="true">#</a> 避免热点</h1><p>热点总是被制造的，并且长远来看不会对自己有任何影响，反而会影响自己的注意力资产，那么主动避免热点就是一个非常重要的选择判断。</p><p>与身边人闲聊时总会进入讨论热点中，最好的方式就是听他们对热点的看法，并保留自己的意见。</p><p>通常我们的很多想法与思考都是由身边人灌输的，例如周末应该出去玩，年轻时假期应该经常出去旅游等等。</p>',9),v=a('<p>所以当周围人谈论热点时，你也要注意回避谈论热点的行为，你要做的就是观察其他人对热点的反应以及记住这些反应，过一段时间再进行反思这些反应是否值当。</p><p>但为了保护自己，你不应该说出你这样的想法，<mark>大多数情况下你应该保持沉默</mark>。</p><h2 id="二十年前的年轻人" tabindex="-1"><a class="header-anchor" href="#二十年前的年轻人" aria-hidden="true">#</a> 二十年前的年轻人</h2><p>杀马特，是二十年前年轻人群体最流行的造型。</p><p>如果你想在此刻变得流行，那么二十年后你看自己的照片——会成为照片里的杀马特。这样很酷吗？</p><p>正确选择与判断从来都不是学校课程考试范围内可衡量的，这些选择与判断充满不确定性，但往往是这种选择与判断决定人生质量，<mark>思考质量决定人生质量</mark>。</p>',6);function N(V,w){const i=o("ExternalLinkIcon"),p=o("RouterLink");return s(),l("div",null,[h,m,u,f,k,c(" more "),B,E,g,x,e("p",null,[e("a",b,[t("中国大陆网络热点事件列表"),n(i)])]),A,e("p",null,[t("这些很多时候并不是你本身的想法，如果你是个上进的人，通常不会经常去娱乐因为这并不是"),n(p,{to:"/posts/thinking/2024/thinking/be-a-producer.html"},{default:d(()=>[t("生产行为")]),_:1}),t("。")]),v])}const L=r(_,[["render",N],["__file","avoid-hot.html.vue"]]);export{L as default};
