import{_ as p}from"./plugin-vue_export-helper-c27b6911.js";import{o,c as r,b as t,d as n,f as a,e as s}from"./app-c429fa5e.js";const d={};function i(m,e){return o(),r("div",null,[e[0]||(e[0]=t("h1",{id:"为公司工作最重要的事情",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#为公司工作最重要的事情","aria-hidden":"true"},"#"),n(" 为公司工作最重要的事情")],-1)),e[1]||(e[1]=t("p",null,"通常我们做事都有个成本，不论是时间成本还是金钱成本...",-1)),e[2]||(e[2]=t("p",null,"所以最近思考了一个问题，即雇佣你时间的公司雇佣你的最终目的是什么。",-1)),a(" more "),e[3]||(e[3]=s("<p>公司雇佣你肯定不是为了让你学习的(像学校)。</p><p><mark>很多应聘者在面试时经常说我虽然不会，但我可以学。</mark></p><p>这样的说法本身是没什么问题，可问题在于公司作为一个商业机构必须能够活在竞争非常激烈的社会中，所以极致的产出，才是关键，有很多应聘者都会的技能可你不会，那么公司没有理由去招聘一个需要学习的人进来。</p><p>这就是关键点，公司是商业机构而不是慈善机构，招一个人最终目的是为公司有产出，即使需要学一点点，但也会看在过去的经验判断这个人大概率可以为公司带来比其薪水更高的价值，所以才会雇佣此人。</p><p>如果一家公司给你的薪水是<code>20k</code>,那么实际上你必须给公司带来<code>50k</code>左右的产出才可以弥补公司的成本(因为公司还有董事会、管理层等，而且相对来看管理者的重要性比底下的员工高得多，通过招聘可以招揽许多技术性人才，但是很难招来优秀的管理者)。</p><p>从这个角度来看，为处于公司中最重要的就是让自己对公司的价值远超于薪水，这样对于公司来说是一笔划算的买卖，与此同时自己也会学到相应的知识，两全其美。</p><p>继续延伸这个话题，通常在一般性的商业公司中有两个最重要的职位：生产者和销售者。</p><p>公司的生产者职责为公司产出可销售产品。</p><p>公司的销售者的职责是向市场销售公司产品。</p><p>在我看来最重要的是生产者和销售者，这两者是公司得以长期生存下去的关键，其他的部分例如前台、hr、法务都是辅助性职务。</p><p>这个话题后续还会继续深入探讨</p><p>etc...</p>",12))])}const c=p(d,[["render",i],["__file","the-most-important-things-in-working.html.vue"]]);export{c as default};
